 $cnt = 0;
foreach ($headers as $lead){
    $section1 = substr($lead, 0, 1);
    $section2 = substr($lead,4,1);
    $type = ($section1 == $section2?'true':'false');
//    echo '<tr><td>'.$lead.'</td><td>'.($section1 == $section2?'true':'false').'</td></tr>'; 
    switch ($type) {
        case 'true':
            $segnum1 = intval(substr($lead, 1,2));
            $segnum2 = intval(substr($lead, 5,2));
            for ($a = $segnum1;$a<=$segnum2;$a++){
               echo '<tr><td>'.$section1.sprintf('%02s', $a).'</td><td>'.$labels[$cnt].'</td></tr>'; 
            }
            break;
        case 'false':
            $segnum1 = intval(substr($lead, 1,2));
            $segnum2 = intval(substr($lead, 5,2));
            for ($a = $segnum1;$a<=99;$a++){
               echo '<tr><td>'.$section1.sprintf('%02s', $a).'</td><td>'.$labels[$cnt].'</td></tr>'; 
            }
            for ($a = 0;$a<=$segnum2;$a++){
               echo '<tr><td>'.$section2.sprintf('%02s', $a).'</td><td>'.$labels[$cnt].'</td></tr>'; 
            }
            break;
    }
    $cnt++;
}
